mapper.insert:
		INSERT INTO tb_rule_m 
        (rule_uuid
        , rule_name        
        , sort_no
        , use_yn
        , chg_dept_name
		, chg_user
		, ovw_cntn
        , rgst_user
        , rgst_dtm
        , upd_user
        , upd_dtm)
  		VALUES 
  		(#{ruleUuid}
  		, #{ruleName} 		
  		, #{sortNo}
  		, #{useYn}
  		, #{chgDeptName}
  		, #{chgUser}
  		, #{ovwCntn}
  		, #{rgstUser}
  		, now()
  		, #{updUser}
  		, now())

mapper.update:
UPDATE tb_rule_m
rule_name = #{ruleName}, ovw_cntn = #{ovwCntn}, use_yn = #{useYn}, chg_dept_name = #{chgDeptName}, chg_user = #{chgUser}, upd_user = #{updUser}, upd_dtm = now()
WHERE rule_uuid = #{ruleUuid}

UPDATE tb_rule_m
rule_msg = #{ruleMsg}, upd_user = #{updUser}, upd_dtm = now()
WHERE rule_uuid = #{ruleUuid}

