mapper.select:
SELECT a.obj_uuid as prod_uuid , a.obj_uuid as obj_uuid , a.valid_start_dtm , a.valid_end_dtm , a.obj_code as prod_item_cd , a.obj_code as obj_code , a.obj_name as prod_item_nm , a.obj_name as obj_name , a.item_code as item_code , d.lctgr_item_name as type , c.mctgr_item_name AS detl_type , b.item_code_name AS sub_type , a.obj_uuid as base_uuid_count , a.obj_uuid as trgt_uuid_count FROM tb_item_mpng_d a JOIN tb_item_c b ON a.item_code = b.item_code JOIN tb_mctgr_item_c c ON b.mctgr_item_code = c.mctgr_item_code JOIN tb_lctgr_item_c d ON c.lctgr_item_code = d.lctgr_item_code
AND d.lctgr_item_code = #{type}
AND c.mctgr_item_code = #{detlType}
AND b.item_code = #{subType}
AND a.obj_code LIKE CONCAT('%', TRIM(UPPER(#{prodItemCd})),'%')
AND UPPER(a.obj_name) LIKE CONCAT('%', TRIM(UPPER(#{prodItemNm})),'%')
AND (a.valid_end_dtm IS NULL OR TIMESTAMP(a.valid_end_dtm) >= NOW())

SELECT c.obj_code AS offr_cd , c.obj_name AS offr_nm
, d.obj_code AS cmp_cd , d.obj_name AS cmp_nm , a.valid_start_dtm AS cmp_vald_strt_dtm , a.valid_end_dtm AS cmp_vald_end_dtm
, e.obj_code AS svc_cd , e.obj_name AS svc_nm , b.valid_start_dtm AS svc_vald_strt_dtm , b.valid_end_dtm AS svc_vald_end_dtm FROM tb_offer_strc_d a LEFT JOIN tb_offer_strc_d b ON a.trgt_uuid = b.base_uuid
LEFT JOIN tb_item_mpng_d c ON a.base_uuid = c.obj_uuid LEFT JOIN tb_item_mpng_d d ON b.base_uuid = d.obj_uuid OR a.trgt_uuid = d.obj_uuid LEFT JOIN tb_item_mpng_d e ON b.trgt_uuid = e.obj_uuid
AND c.obj_uuid = #{offrUuid}
AND d.obj_uuid = #{cmpUuid}
AND e.obj_uuid = #{svcUuid}
ORDER BY cmp_cd, svc_cd
