mapper.select:
SELECT l.lctgr_item_code as largeItemCode, l.lctgr_item_name as largeItemName, m.mctgr_item_code as middleItemCode, m.mctgr_item_name as middleItemName, i.item_code as itemCode, i.item_code_name as itemName, i.item_code as cmcdDetlId, i.item_code_name as cmcdDetlNm, i.sort_no as sortNo, m.sort_no as middle_sort_no, l.sort_no as large_sort_no FROM tb_item_c i RIGHT JOIN tb_mctgr_item_c m on i.mctgr_item_code = m.mctgr_item_code RIGHT JOIN tb_lctgr_item_c l on m.lctgr_item_code = l.lctgr_item_code WHERE 1 = 1 AND i.use_yn = 'Y' AND m.use_yn = 'Y' AND l.use_yn = 'Y'
AND l.lctgr_item_code = #{lItemCode}
AND m.mctgr_item_code = #{mItemCode}
AND i.item_code = #{itemCode}
ORDER BY l.sort_no , m.sort_no , i.sort_no

SELECT DISTINCT im.obj_uuid, im.obj_code, im.obj_name, im.item_code, im.valid_start_dtm, im.valid_end_dtm, os.valid_start_dtm as relationStartDate, os.valid_end_dtm as relationEndDate FROM tb_item_mpng_d im LEFT JOIN tb_offer_strc_d os ON im.obj_uuid = os.trgt_uuid JOIN tb_item_c tic on im.item_code = tic.item_code WHERE tic.lctgr_item_code = 'R'
AND os.base_uuid = #{objUuid}
AND im.item_code = #{itemCode}
AND im.obj_code LIKE CONCAT('%', UPPER(#{objCode}),'%')
AND UPPER(im.obj_name) LIKE CONCAT('%', TRIM(UPPER(#{objName})),'%')
AND (im.valid_end_dtm IS NULL OR TIMESTAMP(im.valid_end_dtm) >= NOW())

mapper.insert:
	 INSERT INTO tb_rsc_add_attr_val_d (
        obj_uuid
        , attr_uuid
        , attr_val
        , rgst_user
        , rgst_dtm
        , upd_user
        , upd_dtm
    )
    VALUES (
        #{objUuid}
        , #{attrUuid}
        , #{attrVal}
        , #{rgstUser}
        , #{rgstDtm}
        , #{updUser}
        , #{updDtm}
    )
	ON DUPLICATE KEY UPDATE
		attr_val = VALUES(attr_val)
		, upd_user = VALUES(upd_user)
		, upd_dtm = VALUES(upd_dtm)

mapper.update:
UPDATE tb_offer_strc_d os

	os.valid_end_dtm = DATE_FORMAT(DATE(#{validEndDtm}) , '%Y-%m-%d 23:59:59'),
	os.upd_user = #{updUser},
	os.upd_dtm = #{updDtm}

WHERE os.trgt_uuid = #{objUuid}

	AND (TIMESTAMP(os.valid_end_dtm) > DATE_FORMAT(DATE(#{validEndDtm}) , '%Y-%m-%d 23:59:59') OR os.valid_end_dtm IS NULL)

mapper.select:
SELECT a.obj_uuid , a.obj_code , a.obj_name , a.item_code , a.valid_start_dtm , a.valid_end_dtm , a.dplc_trgt_uuid , a.chg_dept_name , a.chg_user , a.ovw_cntn , a.rgst_user , a.rgst_dtm , a.upd_user , a.upd_dtm , b.lctgr_item_code , b.mctgr_item_code FROM tb_item_mpng_d a JOIN tb_item_c b ON a.item_code = b.item_code
b.lctgr_item_code = 'R'
AND b.mctgr_item_code = #{mctgrItemCode}
AND a.item_code = #{itemCode}
AND UPPER(a.obj_code) LIKE CONCAT('%', UPPER(#{objCode}),'%')
AND UPPER(a.obj_name) LIKE CONCAT('%', UPPER(#{objName}),'%')
AND
EXISTS ( SELECT 1 FROM tb_add_attr_header_m c LEFT JOIN tb_rsc_add_attr_val_d d ON c.attr_uuid = d.attr_uuid AND d.obj_uuid = a.obj_uuid WHERE c.item_code = a.item_code AND c.attr_uuid = #{param.fieldName}
AND UPPER(d.attr_val) LIKE CONCAT('%', UPPER(#{param.fieldValue}),'%')
AND d.attr_val IN
#{value}
AND EXISTS ( SELECT 1 FROM tb_multi_code_add_attr_val_d tmcaavd WHERE tmcaavd.attr_uuid = c.attr_uuid AND tmcaavd.obj_uuid = a.obj_uuid AND tmcaavd.attr_val IN
#{val}
)
AND d.attr_val >= #{param.fieldValueMin}
AND d.attr_val <= #{param.fieldValueMax}
AND CAST(COALESCE(d.attr_val, '0') AS DOUBLE) >= CAST(#{param.fieldValueMin} AS DOUBLE) AND d.attr_val IS NOT NULL
AND CAST(COALESCE(d.attr_val, '0') AS DOUBLE) <= CAST(#{param.fieldValueMax} AS DOUBLE) AND d.attr_val IS NOT NULL
)
