mapper.select:
SELECT tupm.user_id, tupm.obj_uuid, tupm.valid_start_dtm, tupm.valid_end_dtm ,tupm.rgst_user, tupm.rgst_dtm, tupm.upd_user, tupm.upd_dtm FROM tb_user_pocket_m tupm WHERE 1 = 1
and tupm.obj_uuid = #{uuid}
and tupm.user_id = #{userId}
and (tupm.valid_end_dtm IS NULL OR TIMESTAMP(tupm.valid_end_dtm) >= NOW())

mapper.update:
UPDATE tb_user_pocket_m tupm

	tupm.valid_end_dtm = #{validEndDtm}

where tupm.obj_uuid = #{uuid} and tupm.user_id = #{userId}
