mapper.update:
	UPDATE tb_dsbd_user_set_m

    attr_val = #{attrVal}

    WHERE 1 = 1

    and dsbd_view_uuid = #{dsbdViewUuid}


    and user_id = #{userId}


    and seq_no = #{seqNo}


    and attr_key = #{attrKey}

mapper.delete:
	DELETE
    FROM tb_dsbd_user_set_m where 1 = 1

    and user_id = #{userId}


    and seq_no = #{seqNo}


    and dsbd_view_uuid = #{dsbdViewUuid}


    and attr_key = #{attrKey}


    and attr_val = #{attrVal}

mapper.select:
SELECT
dsbd_view_uuid, user_id, seq_no, attr_val, attr_key, rgst_user, rgst_dtm, upd_user, upd_dtm
FROM tb_dsbd_user_set_m
where 1 = 1

and dsbd_view_uuid = #{dsbdViewUuid}


and user_id = #{userId}


and seq_no = #{seqNo}


and attr_key = #{attrKey}


and attr_val = #{attrVal}


order by

${item}

SELECT count(*)
FROM tb_dsbd_user_set_m
WHERE 1 = 1

and dsbd_view_uuid = #{dsbdViewUuid}


and user_id = #{userId}


and seq_no = #{seqNo}


and attr_key = #{attrKey}


and attr_val = #{attrVal}

SELECT seq_no
FROM tb_dsbd_user_set_m
where dsbd_view_uuid = #{dsbdViewUuid}
and user_id = #{userId}
and attr_key = #{attrKey}
order by seq_no desc

SELECT attr_val
FROM tb_dsbd_user_set_m
where dsbd_view_uuid = #{dsbdViewUuid}
and user_id = #{userId} and attr_key = #{attrKey}
and EXTRACT(MONTH FROM TO_DATE(attr_val, 'YYYY-MM-DD')) = #{monthInput} and EXTRACT(YEAR FROM TO_DATE(attr_val, 'YYYY-MM-DD')) = #{yearInput};
